mapper.select:
	select * from w_join


  		resume_id=#{resumeId}


      and id=#{id}


      and resume_id=#{resumeId}


      and user_name=#{userName}


     and user_id=#{userId}


     and recruitment_info_id=#{recruitmentInfoId}


     and recruitment_info_createBy=#{recruitmentInfoCreateby}


     and recruitment_info_createBy_name=#{recruitmentInfoCreatebyName}


     and createTime=#{createtime}


     and remark=#{remark}


     and state=#{state}


    	limit ${start},${end}

select count(1) from w_join


		resume_id=#{resumeId}


    and id=#{id}


    and resume_id=#{resumeId}


    and user_name=#{userName}


   and user_id=#{userId}


   and recruitment_info_id=#{recruitmentInfoId}


   and recruitment_info_createBy=#{recruitmentInfoCreateby}


   and recruitment_info_createBy_name=#{recruitmentInfoCreatebyName}


   and createTime=#{createtime}


   and remark=#{remark}


   and state=#{state}
